package homework_1;

public class V12_For_Döngüsüyle_Çalışmak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<=50;i++) {
			System.out.println(i);
		}
		System.out.println("Döngü Bitti");
		
		for(int i=0;i<=20;i+=2) {
			System.out.println(i);
		}
		System.out.println("Döngü Bitti");

	}

}
